```csharp  
using System;  
using System.Globalization;  
using System.Speech.Recognition;  
using System.Threading;  
  
namespace SreEmulateRecognizeAsync  
{  
  class Program  
  {  
    // Indicate when an asynchronous operation is finished.  
    static bool completed;  
  
    static void Main(string[] args)  
    {  
      using (SpeechRecognitionEngine recognizer =  
        new SpeechRecognitionEngine(new CultureInfo("en-US")))  
      {  
        // Load grammars.  
        recognizer.LoadGrammar(CreateNameGrammar("Smith"));  
        recognizer.LoadGrammar(CreateNameGrammar("Jones"));  
  
        // Configure the audio input.  
        recognizer.SetInputToNull();  
  
        // Add event handlers for the events raised by the  
        // EmulateRecognizeAsync method.  
        recognizer.SpeechDetected +=  
          new EventHandler<SpeechDetectedEventArgs>(  
            SpeechDetectedHandler);  
        recognizer.SpeechHypothesized +=  
          new EventHandler<SpeechHypothesizedEventArgs>(  
            SpeechHypothesizedHandler);  
        recognizer.SpeechRecognitionRejected +=  
          new EventHandler<SpeechRecognitionRejectedEventArgs>(  
            SpeechRecognitionRejectedHandler);  
        recognizer.SpeechRecognized +=  
          new EventHandler<SpeechRecognizedEventArgs>(  
            SpeechRecognizedHandler);  
        recognizer.EmulateRecognizeCompleted +=  
          new EventHandler<EmulateRecognizeCompletedEventArgs>(  
            EmulateRecognizeCompletedHander);  
  
        // Start four asynchronous emulated recognition operations.  
        TestRecognizeAsync(recognizer, "Smith");  
        TestRecognizeAsync(recognizer, "Jones");  
        TestRecognizeAsync(recognizer, "Mister");  
        TestRecognizeAsync(recognizer, "Mister Smith");  
      }  
  
      Console.WriteLine("press any key to exit...");  
      Console.ReadKey(true);  
    }  
  
    // Create a simple name grammar.  
    // Set the grammar name to the surname.  
    private static Grammar CreateNameGrammar(string surname)  
    {  
      GrammarBuilder builder = new GrammarBuilder("mister", 0, 1);  
      builder.Append(surname);  
  
      Grammar nameGrammar = new Grammar(builder);  
      nameGrammar.Name = surname;  
  
      return nameGrammar;  
    }  
  
    // Send emulated input to the recognizer for asynchronous  
    // recognition.  
    private static void TestRecognizeAsync(  
      SpeechRecognitionEngine recognizer, string input)  
    {  
      completed = false;  
  
      Console.WriteLine("TestRecognizeAsync(\"{0}\")...", input);  
      recognizer.EmulateRecognizeAsync(input);  
  
      // Wait for the operation to complete.  
      while (!completed)  
      {  
        Thread.Sleep(333);  
      }  
  
      Console.WriteLine(" Done.");  
      Console.WriteLine();  
    }  
  
    static void SpeechDetectedHandler(  
      object sender, SpeechDetectedEventArgs e)  
    {  
      Console.WriteLine(" SpeechDetected event raised.");  
    }  
  
    static void SpeechHypothesizedHandler(  
      object sender, SpeechHypothesizedEventArgs e)  
    {  
      Console.WriteLine(" SpeechHypothesized event raised.");  
      if (e.Result != null)  
      {  
        Console.WriteLine("  Grammar = {0}; Text = {1}",  
          e.Result.Grammar.Name ?? "<none>", e.Result.Text);  
      }  
      else  
      {  
        Console.WriteLine("  No recognition result available.");  
      }  
    }  
  
    // Handle events.  
    static void SpeechRecognitionRejectedHandler(  
      object sender, SpeechRecognitionRejectedEventArgs e)  
    {  
      Console.WriteLine(" SpeechRecognitionRejected event raised.");  
      if (e.Result != null)  
      {  
        string grammarName;  
        if (e.Result.Grammar != null)  
        {  
          grammarName = e.Result.Grammar.Name ?? "<none>";  
        }  
        else  
        {  
          grammarName = "<not available>";  
        }  
        Console.WriteLine("  Grammar = {0}; Text = {1}",  
          grammarName, e.Result.Text);  
      }  
      else  
      {  
        Console.WriteLine("  No recognition result available.");  
      }  
    }  
  
    static void SpeechRecognizedHandler(  
      object sender, SpeechRecognizedEventArgs e)  
    {  
      Console.WriteLine(" SpeechRecognized event raised.");  
      if (e.Result != null)  
      {  
        Console.WriteLine("  Grammar = {0}; Text = {1}",  
          e.Result.Grammar.Name ?? "<none>", e.Result.Text );  
      }  
      else  
      {  
        Console.WriteLine("  No recognition result available.");  
      }  
    }  
  
    static void EmulateRecognizeCompletedHander(  
      object sender, EmulateRecognizeCompletedEventArgs e)  
    {  
      Console.WriteLine(" EmulateRecognizeCompleted event raised.");  
  
      if (e.Error != null)  
      {  
        Console.WriteLine("  {0} exception encountered: {1}:",  
          e.Error.GetType().Name, e.Error.Message);  
      }  
      else if (e.Cancelled)  
      {  
        Console.WriteLine("  Operation cancelled.");  
      }  
      else if (e.Result != null)  
      {  
        Console.WriteLine("  Grammar = {0}; Text = {1}",  
          e.Result.Grammar.Name ?? "<none>", e.Result.Text);  
      }  
      else  
      {  
        Console.WriteLine("  No recognition result available.");  
      }  
  
      completed = true;  
    }  
  }  
}  
  
```

  
  
## Remarks  
 <span data-ttu-id="65a8d-342">Ogni <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeAsync%2A> metodo avvia un'operazione di riconoscimento asincrona.</span><span class="sxs-lookup"><span data-stu-id="65a8d-342">Each <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeAsync%2A> method begins an asynchronous recognition operation.</span></span> <span data-ttu-id="65a8d-343">Il <xref:System.Speech.Recognition.SpeechRecognitionEngine> genera il <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> eventi quando completa l'operazione asincrona.</span><span class="sxs-lookup"><span data-stu-id="65a8d-343">The <xref:System.Speech.Recognition.SpeechRecognitionEngine> raises the <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> event when it finalizes the asynchronous operation.</span></span>  
  
 <span data-ttu-id="65a8d-344">Il <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeAsync%2A> operazione può generare il <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechDetected>, <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechHypothesized>, <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognitionRejected>, e <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> eventi.</span><span class="sxs-lookup"><span data-stu-id="65a8d-344">The <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeAsync%2A> operation can raise the <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechDetected>, <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechHypothesized>, <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognitionRejected>, and <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> events.</span></span> <span data-ttu-id="65a8d-345">Il <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> eventi sono l'ultimo evento di questo tipo che genera la sospensione del riconoscimento per l'operazione specificata.</span><span class="sxs-lookup"><span data-stu-id="65a8d-345">The <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> event is the last such event that the recognizer raises for a given operation.</span></span>  
  
 <span data-ttu-id="65a8d-346">Se il riconoscimento emulato ha esito positivo, è possibile accedere al risultato del riconoscimento usando uno dei seguenti:</span><span class="sxs-lookup"><span data-stu-id="65a8d-346">If emulated recognition was successful, you can access the recognition result using the either of the following:</span></span>  
  
-   <span data-ttu-id="65a8d-347">Il <xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs.Result%2A> proprietà nel <xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs> oggetto nel gestore per il <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> evento.</span><span class="sxs-lookup"><span data-stu-id="65a8d-347">The <xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs.Result%2A> property in the <xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs> object in the handler for the <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> event.</span></span>  
  
-   <span data-ttu-id="65a8d-348"><xref:System.Speech.Recognition.RecognitionEventArgs.Result%2A> proprietà nel <xref:System.Speech.Recognition.SpeechRecognizedEventArgs> oggetto nel gestore per il <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> evento.</span><span class="sxs-lookup"><span data-stu-id="65a8d-348"><xref:System.Speech.Recognition.RecognitionEventArgs.Result%2A> property in the <xref:System.Speech.Recognition.SpeechRecognizedEventArgs> object in the handler for the <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> event.</span></span>  
  
 <span data-ttu-id="65a8d-349">Se il riconoscimento emulato non è riuscito, il <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> non viene generato e il <xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs.Result%2A> sarà null.</span><span class="sxs-lookup"><span data-stu-id="65a8d-349">If emulated recognition was not successful, the <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> event is not raised and the <xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs.Result%2A> will be null.</span></span>  
  
 <span data-ttu-id="65a8d-350"><xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs> deriva da <xref:System.ComponentModel.AsyncCompletedEventArgs>.</span><span class="sxs-lookup"><span data-stu-id="65a8d-350"><xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs> derives from <xref:System.ComponentModel.AsyncCompletedEventArgs>.</span></span>  
  
 <span data-ttu-id="65a8d-351"><xref:System.Speech.Recognition.SpeechRecognizedEventArgs> deriva da <xref:System.Speech.Recognition.RecognitionEventArgs>.</span><span class="sxs-lookup"><span data-stu-id="65a8d-351"><xref:System.Speech.Recognition.SpeechRecognizedEventArgs> derives from <xref:System.Speech.Recognition.RecognitionEventArgs>.</span></span>  
  
 <span data-ttu-id="65a8d-352">Quando si crea un delegato di <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted>, si identifica il metodo con cui gestire l'evento.</span><span class="sxs-lookup"><span data-stu-id="65a8d-352">When you create an <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> delegate, you identify the method that will handle the event.</span></span> <span data-ttu-id="65a8d-353">Per associare l'evento al gestore eventi in uso, aggiungere all'evento un'istanza del delegato.</span><span class="sxs-lookup"><span data-stu-id="65a8d-353">To associate the event with your event handler, add an instance of the delegate to the event.</span></span> <span data-ttu-id="65a8d-354">Il gestore eventi viene chiamato ogni volta che si verifica l'evento, a meno che non venga rimosso il delegato.</span><span class="sxs-lookup"><span data-stu-id="65a8d-354">The event handler is called whenever the event occurs, unless you remove the delegate.</span></span> <span data-ttu-id="65a8d-355">Per altre informazioni sui delegati del gestore eventi, vedere [eventi e delegati](http://go.microsoft.com/fwlink/?LinkId=162418).</span><span class="sxs-lookup"><span data-stu-id="65a8d-355">For more information about event-handler delegates, see [Events and Delegates](http://go.microsoft.com/fwlink/?LinkId=162418).</span></span>  
  
   
  
## Examples  
 <span data-ttu-id="65a8d-356">Nell'esempio seguente fa parte di un'applicazione console che carica una grammatica di riconoscimento vocale e illustra input emulato asincrona, i risultati di riconoscimento associati e gli eventi associati generati dal riconoscimento vocale.</span><span class="sxs-lookup"><span data-stu-id="65a8d-356">The following example is part of a console application that loads a speech recognition grammar and demonstrates asynchronous emulated input, the associated recognition results, and the associated events raised by the speech recognizer.</span></span>  
  
```  
using System;  
using System.Speech.Recognition;  
using System.Threading;  
  
namespace InProcessRecognizer  
{  
  class Program  
  {  
    // Indicate whether the asynchronous emulate recognition  
    // operation has completed.  
    static bool completed;  
  
    static void Main(string[] args)  
    {  
  
      // Initialize an instance of an in-process recognizer.  
      using (SpeechRecognitionEngine recognizer =   
        new SpeechRecognitionEngine(new System.Globalization.CultureInfo("en-US")))  
      {  
        // Create and load a sample grammar.  
        Grammar testGrammar =  
          new Grammar(new GrammarBuilder("testing testing"));  
        testGrammar.Name = "Test Grammar";  
        recognizer.LoadGrammar(testGrammar);  
  
        // Attach event handlers for recognition events.  
        recognizer.SpeechRecognized +=  
          new EventHandler<SpeechRecognizedEventArgs>(SpeechRecognizedHandler);  
        recognizer.EmulateRecognizeCompleted +=  
          new EventHandler<EmulateRecognizeCompletedEventArgs>(  
            EmulateRecognizeCompletedHandler);  
  
        completed = false;  
  
        // This EmulateRecognizeAsync call mathches the grammar  
        // and generates a SpeechRecognized event.  
        recognizer.EmulateRecognizeAsync("testing testing");  
  
        // Wait for the asynchronous operation to complete.  
        while (!completed)  
        {  
          Thread.Sleep(333);  
        }  
  
        completed = false;  
  
        // This EmulateRecognizeAsync call does not match the grammar  
        // or generate a SpeechRecognized event.  
        recognizer.EmulateRecognizeAsync("testing one two three");  
  
        // Wait for the asynchronous operation to complete.  
        while (!completed)  
        {  
          Thread.Sleep(333);  
        }  
      }  
  
      Console.WriteLine();  
      Console.WriteLine("Press any key to exit...");  
      Console.ReadKey();  
    }  
  
    // Handle the SpeechRecognized event.  
    static void SpeechRecognizedHandler(  
      object sender, SpeechRecognizedEventArgs e)  
    {  
      if (e.Result != null)  
      {  
        Console.WriteLine("Result of 1st call to EmulateRecognizeAsync = {0}",  
          e.Result.Text ?? "<no text>");  
        Console.WriteLine();  
      }  
      else  
      {  
        Console.WriteLine("No recognition result");  
      }  
    }  
  
    // Handle the EmulateRecognizeCompleted event.  
    static void EmulateRecognizeCompletedHandler(  
      object sender, EmulateRecognizeCompletedEventArgs e)  
    {  
      if (e.Result == null)  
      {  
        Console.WriteLine("Result of 2nd call to EmulateRecognizeAsync = No result generated.");  
      }  
  
      // Indicate the asynchronous operation is complete.  
      completed = true;  
    }  
  }  
}  
  
```

  
  
## Examples  
 <span data-ttu-id="65a8d-377">L'esempio seguente vengono restituite informazioni nella console per ogni grammatica di riconoscimento vocale attualmente caricata da un riconoscimento vocale.</span><span class="sxs-lookup"><span data-stu-id="65a8d-377">The following example outputs information to the console for each speech recognition grammar that is currently loaded by a speech recognizer.</span></span>  
  
> [!IMPORTANT]
>  <span data-ttu-id="65a8d-378">Copiare l'insieme di grammatica per evitare errori se la raccolta viene modificata mentre questo metodo enumera gli elementi della raccolta.</span><span class="sxs-lookup"><span data-stu-id="65a8d-378">Copy the grammar collection to avoid errors if the collection is modified while this method enumerates the elements of the collection.</span></span>  
  
```csharp  
  
private static void ListGrammars(SpeechRecognitionEngine recognizer)  
{  
  string qualifier;  
  List<Grammar> grammars = new List<Grammar>(recognizer.Grammars);  
  foreach (Grammar g in grammars)  
  {  
    qualifier = (g.Enabled) ? "enabled" : "disabled";  
  
    Console.WriteLine("Grammar {0} is loaded and is {1}.",  
      g.Name, qualifier);  
  }  
}  
```

  
  
## Remarks  
 <span data-ttu-id="65a8d-381">Ogni riconoscimento vocale è disponibile un algoritmo per distinguere tra silenzio e riconoscimento vocale.</span><span class="sxs-lookup"><span data-stu-id="65a8d-381">Each speech recognizer has an algorithm to distinguish between silence and speech.</span></span> <span data-ttu-id="65a8d-382">Se l'input di riconoscimento è silenzio durante il periodo di timeout di inattività iniziale, il riconoscimento completa tale operazione di riconoscimento.</span><span class="sxs-lookup"><span data-stu-id="65a8d-382">If the recognizer input is silence during the initial silence timeout period, then the recognizer finalizes that recognition operation.</span></span>  
  
-   <span data-ttu-id="65a8d-383">Per le operazioni di riconoscimento asincrona ed emulazione, il riconoscimento genera il <xref:System.Speech.Recognition.SpeechRecognitionEngine.RecognizeCompleted> evento, in cui la <xref:System.Speech.Recognition.RecognizeCompletedEventArgs.InitialSilenceTimeout%2A?displayProperty=nameWithType> proprietà è `true`e il <xref:System.Speech.Recognition.RecognizeCompletedEventArgs.Result%2A?displayProperty=nameWithType> proprietà è `null`.</span><span class="sxs-lookup"><span data-stu-id="65a8d-383">For asynchronous recognition operations and emulation, the recognizer raises the <xref:System.Speech.Recognition.SpeechRecognitionEngine.RecognizeCompleted> event, where the <xref:System.Speech.Recognition.RecognizeCompletedEventArgs.InitialSilenceTimeout%2A?displayProperty=nameWithType> property is `true`, and the <xref:System.Speech.Recognition.RecognizeCompletedEventArgs.Result%2A?displayProperty=nameWithType> property is `null`.</span></span>  
  
-   <span data-ttu-id="65a8d-384">Per operazioni di riconoscimento sincrono ed emulazione, restituisce il riconoscimento `null`, anziché un valore valido <xref:System.Speech.Recognition.RecognitionResult>.</span><span class="sxs-lookup"><span data-stu-id="65a8d-384">For synchronous recognition operations and emulation, the recognizer returns `null`, instead of a valid <xref:System.Speech.Recognition.RecognitionResult>.</span></span>  
  
 <span data-ttu-id="65a8d-385">Se l'intervallo di timeout di inattività iniziale è impostato su 0, il riconoscimento non esegue una verifica di timeout di inattività iniziale.</span><span class="sxs-lookup"><span data-stu-id="65a8d-385">If the initial silence timeout interval is set to 0, the recognizer does not perform an initial silence timeout check.</span></span> <span data-ttu-id="65a8d-386">L'intervallo di timeout può essere qualsiasi valore non negativo.</span><span class="sxs-lookup"><span data-stu-id="65a8d-386">The timeout interval can be any non-negative value.</span></span> <span data-ttu-id="65a8d-387">Il valore predefinito è 0 secondi.</span><span class="sxs-lookup"><span data-stu-id="65a8d-387">The default is 0 seconds.</span></span>  
  
   
  
## Examples  
 <span data-ttu-id="65a8d-388">Nell'esempio seguente mostra parte di un'applicazione console che illustra il riconoscimento vocale di base.</span><span class="sxs-lookup"><span data-stu-id="65a8d-388">The following example shows part of a console application that demonstrates basic speech recognition.</span></span> <span data-ttu-id="65a8d-389">Nell'esempio viene impostata la <xref:System.Speech.Recognition.SpeechRecognitionEngine.BabbleTimeout%2A> e <xref:System.Speech.Recognition.SpeechRecognitionEngine.InitialSilenceTimeout%2A> delle proprietà di un <xref:System.Speech.Recognition.SpeechRecognitionEngine> prima dell'avvio di riconoscimento vocale.</span><span class="sxs-lookup"><span data-stu-id="65a8d-389">The example sets the <xref:System.Speech.Recognition.SpeechRecognitionEngine.BabbleTimeout%2A> and <xref:System.Speech.Recognition.SpeechRecognitionEngine.InitialSilenceTimeout%2A> properties of a <xref:System.Speech.Recognition.SpeechRecognitionEngine> before initiating speech recognition.</span></span> <span data-ttu-id="65a8d-390">Gestori per il riconoscimento vocale <xref:System.Speech.Recognition.SpeechRecognitionEngine.AudioStateChanged> e <xref:System.Speech.Recognition.SpeechRecognitionEngine.RecognizeCompleted> eventi di output delle informazioni di evento nella console per dimostrare come la <xref:System.Speech.Recognition.SpeechRecognitionEngine.InitialSilenceTimeout%2A> delle proprietà di un <xref:System.Speech.Recognition.SpeechRecognitionEngine> tali proprietà influiscono sulle operazioni di riconoscimento.</span><span class="sxs-lookup"><span data-stu-id="65a8d-390">Handlers for the speech recognizer's <xref:System.Speech.Recognition.SpeechRecognitionEngine.AudioStateChanged> and <xref:System.Speech.Recognition.SpeechRecognitionEngine.RecognizeCompleted> events output event information to the console to demonstrate how the <xref:System.Speech.Recognition.SpeechRecognitionEngine.InitialSilenceTimeout%2A> properties of a <xref:System.Speech.Recognition.SpeechRecognitionEngine> properties affect recognition operations.</span></span>  
  
```csharp  
  
using System;  
using System.Speech.Recognition;  
  
namespace SpeechRecognitionApp  
{  
  class Program  
  {  
    static void Main(string[] args)  
    {  
  
      // Initialize an in-process speech recognizer.  
      using (SpeechRecognitionEngine recognizer =  
        new SpeechRecognitionEngine(  
          new System.Globalization.CultureInfo("en-US")))  
      {  
        // Load a Grammar object.  
        recognizer.LoadGrammar(CreateServicesGrammar("FindServices"));  
  
        // Add event handlers.  
        recognizer.AudioStateChanged +=  
          new EventHandler<AudioStateChangedEventArgs>(  
            AudioStateChangedHandler);  
        recognizer.RecognizeCompleted +=  
          new EventHandler<RecognizeCompletedEventArgs>(  
            RecognizeCompletedHandler);  
  
        // Configure input to the speech recognizer.  
        recognizer.SetInputToDefaultAudioDevice();  
  
        recognizer.InitialSilenceTimeout = TimeSpan.FromSeconds(3);  
        recognizer.BabbleTimeout = TimeSpan.FromSeconds(2);  
        recognizer.EndSilenceTimeout = TimeSpan.FromSeconds(1);  
        recognizer.EndSilenceTimeoutAmbiguous = TimeSpan.FromSeconds(1.5);  
  
        Console.WriteLine("BabbleTimeout: {0}", recognizer.BabbleTimeout);  
        Console.WriteLine("InitialSilenceTimeout: {0}", recognizer.InitialSilenceTimeout);  
        Console.WriteLine("EndSilenceTimeout: {0}", recognizer.EndSilenceTimeout);  
        Console.WriteLine("EndSilenceTimeoutAmbiguous: {0}", recognizer.EndSilenceTimeoutAmbiguous);  
        Console.WriteLine();  
  
        // Start asynchronous speech recognition.  
        recognizer.RecognizeAsync(RecognizeMode.Single);  
  
        // Keep the console window open.  
        while (true)  
        {  
          Console.ReadLine();  
        }  
      }  
    }  
  
    // Create a grammar and build it into a Grammar object.   
    static Grammar CreateServicesGrammar(string grammarName)  
    {  
  
      // Create a grammar for finding services in different cities.  
      Choices services = new Choices(new string[] { "restaurants", "hotels", "gas stations" });  
      Choices cities = new Choices(new string[] { "Seattle", "Boston", "Dallas" });  
  
      GrammarBuilder findServices = new GrammarBuilder("Find");  
      findServices.Append(services);  
      findServices.Append("near");  
      findServices.Append(cities);  
  
      // Create a Grammar object from the GrammarBuilder..  
      Grammar servicesGrammar = new Grammar(findServices);  
      servicesGrammar.Name = ("FindServices");  
      return servicesGrammar;  
    }  
  
    // Handle the AudioStateChanged event.  
    static void AudioStateChangedHandler(  
      object sender, AudioStateChangedEventArgs e)  
    {  
      Console.WriteLine("AudioStateChanged ({0}): {1}",  
        DateTime.Now.ToString("mm:ss.f"), e.AudioState);  
    }  
  
    // Handle the RecognizeCompleted event.  
    static void RecognizeCompletedHandler(  
      object sender, RecognizeCompletedEventArgs e)  
    {  
      Console.WriteLine("RecognizeCompleted ({0}):",  
        DateTime.Now.ToString("mm:ss.f"));  
  
      string resultText;  
      if (e.Result != null) { resultText = e.Result.Text; }  
      else { resultText = "<null>"; }  
  
      Console.WriteLine(  
        " BabbleTimeout: {0}; InitialSilenceTimeout: {1}; Result text: {2}",  
        e.BabbleTimeout, e.InitialSilenceTimeout, resultText);  
      if (e.Error != null)  
      {  
        Console.WriteLine(" Exception message: ", e.Error.Message);  
      }  
  
      // Start the next asynchronous recognition operation.  
      ((SpeechRecognitionEngine)sender).RecognizeAsync(RecognizeMode.Single);  
    }  
  }  
}  
  
```

  
  
## Remarks  
 <span data-ttu-id="65a8d-394">Per ottenere informazioni sul riconoscimento corrente, usare il <xref:System.Speech.Recognition.SpeechRecognitionEngine.RecognizerInfo%2A> proprietà.</span><span class="sxs-lookup"><span data-stu-id="65a8d-394">To get information about the current recognizer, use the <xref:System.Speech.Recognition.SpeechRecognitionEngine.RecognizerInfo%2A> property.</span></span>  
  
   
  
## Examples  
 <span data-ttu-id="65a8d-395">Nell'esempio seguente mostra parte di un'applicazione console che illustra il riconoscimento vocale di base.</span><span class="sxs-lookup"><span data-stu-id="65a8d-395">The following example shows part of a console application that demonstrates basic speech recognition.</span></span> <span data-ttu-id="65a8d-396">L'esempio Usa la raccolta restituita dal <xref:System.Speech.Recognition.SpeechRecognitionEngine.InstalledRecognizers%2A> metodo per trovare un riconoscimento vocale che supporta la lingua inglese.</span><span class="sxs-lookup"><span data-stu-id="65a8d-396">The example uses the collection returned by the <xref:System.Speech.Recognition.SpeechRecognitionEngine.InstalledRecognizers%2A> method to find a speech recognizer that supports the English language.</span></span>  
  
```csharp  
using System;  
using System.Speech.Recognition;  
  
namespace SpeechRecognitionApp  
{  
  class Program  
  {  
    static void Main(string[] args)  
    {  
  
      // Select a speech recognizer that supports English.  
      RecognizerInfo info = null;  
      foreach (RecognizerInfo ri in SpeechRecognitionEngine.InstalledRecognizers())  
      {  
        if (ri.Culture.TwoLetterISOLanguageName.Equals("en"))  
        {  
          info = ri;  
          break;  
        }  
      }  
      if (info == null) return;  
  
      // Create the selected recognizer.  
      using (SpeechRecognitionEngine recognizer =  
        new SpeechRecognitionEngine(info))  
      {  
  
        // Create and load a dictation grammar.  
        recognizer.LoadGrammar(new DictationGrammar());  
  
        // Add a handler for the speech recognized event.  
        recognizer.SpeechRecognized +=   
          new EventHandler<SpeechRecognizedEventArgs>(recognizer_SpeechRecognized);  
  
        // Configure input to the speech recognizer.  
        recognizer.SetInputToDefaultAudioDevice();  
  
        // Start asynchronous, continuous speech recognition.  
        recognizer.RecognizeAsync(RecognizeMode.Multiple);  
  
        // Keep the console window open.  
        while (true)  
        {  
          Console.ReadLine();  
        }  
      }  
    }  
  
    // Handle the SpeechRecognized event.  
    static void recognizer_SpeechRecognized(object sender, SpeechRecognizedEventArgs e)  
    {  
      Console.WriteLine("Recognized text: " + e.Result.Text);  
    }  
  }  
}  
```

  
  
## Remarks  
 <span data-ttu-id="65a8d-399">Il riconoscimento genera un'eccezione se il <xref:System.Speech.Recognition.Grammar> oggetto è già caricata, viene caricato in modo asincrono o non è riuscito a caricare in qualsiasi sistema di riconoscimento.</span><span class="sxs-lookup"><span data-stu-id="65a8d-399">The recognizer throws an exception if the <xref:System.Speech.Recognition.Grammar> object is already loaded, is being asynchronously loaded, or has failed to load into any recognizer.</span></span> <span data-ttu-id="65a8d-400">Non è possibile caricare lo stesso <xref:System.Speech.Recognition.Grammar> oggetto in più istanze di <xref:System.Speech.Recognition.SpeechRecognitionEngine>.</span><span class="sxs-lookup"><span data-stu-id="65a8d-400">You cannot load the same <xref:System.Speech.Recognition.Grammar> object into multiple instances of <xref:System.Speech.Recognition.SpeechRecognitionEngine>.</span></span> <span data-ttu-id="65a8d-401">In alternativa, creare una nuova <xref:System.Speech.Recognition.Grammar> per ciascun <xref:System.Speech.Recognition.SpeechRecognitionEngine> istanza.</span><span class="sxs-lookup"><span data-stu-id="65a8d-401">Instead, create a new <xref:System.Speech.Recognition.Grammar> object for each <xref:System.Speech.Recognition.SpeechRecognitionEngine> instance.</span></span>  
  
 <span data-ttu-id="65a8d-402">Se il riconoscimento è in esecuzione, le applicazioni devono utilizzare <xref:System.Speech.Recognition.SpeechRecognitionEngine.RequestRecognizerUpdate%2A> sospendere il motore di riconoscimento vocale prima il caricamento, scaricamento, abilitazione o la disabilitazione di una grammatica.</span><span class="sxs-lookup"><span data-stu-id="65a8d-402">If the recognizer is running, applications must use <xref:System.Speech.Recognition.SpeechRecognitionEngine.RequestRecognizerUpdate%2A> to pause the speech recognition engine before loading, unloading,  enabling, or disabling a grammar.</span></span>  
  
 <span data-ttu-id="65a8d-403">Quando si carica una grammatica, si è abilitato per impostazione predefinita.</span><span class="sxs-lookup"><span data-stu-id="65a8d-403">When you load a grammar, it is enabled by default.</span></span> <span data-ttu-id="65a8d-404">Per disabilitare una grammatica di caricamento, usare il <xref:System.Speech.Recognition.Grammar.Enabled%2A> proprietà.</span><span class="sxs-lookup"><span data-stu-id="65a8d-404">To disable a loaded grammar, use the <xref:System.Speech.Recognition.Grammar.Enabled%2A> property.</span></span>  
  
 <span data-ttu-id="65a8d-405">Per caricare un <xref:System.Speech.Recognition.Grammar> oggetto in modo asincrono, usare il <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarAsync%2A> (metodo).</span><span class="sxs-lookup"><span data-stu-id="65a8d-405">To load a <xref:System.Speech.Recognition.Grammar> object asynchronously, use the <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarAsync%2A> method.</span></span>  
  
   
  
## Examples  
 <span data-ttu-id="65a8d-406">Nell'esempio seguente mostra parte di un'applicazione console che illustra il riconoscimento vocale di base.</span><span class="sxs-lookup"><span data-stu-id="65a8d-406">The following example shows part of a console application that demonstrates basic speech recognition.</span></span> <span data-ttu-id="65a8d-407">Nell'esempio viene creato un <xref:System.Speech.Recognition.DictationGrammar> e li carica in un riconoscimento vocale.</span><span class="sxs-lookup"><span data-stu-id="65a8d-407">The example creates a <xref:System.Speech.Recognition.DictationGrammar> and loads it into a speech recognizer.</span></span>  
  
```csharp  
using System;  
using System.Speech.Recognition;  
  
namespace SpeechRecognitionApp  
{  
  class Program  
  {  
    static void Main(string[] args)  
    {  
  
      // Create an in-process speech recognizer for the en-US locale.  
      using (  
      SpeechRecognitionEngine recognizer =  
        new SpeechRecognitionEngine(  
          new System.Globalization.CultureInfo("en-US")))  
      {  
  
        // Create and load a dictation grammar.  
        recognizer.LoadGrammar(new DictationGrammar());  
  
        // Add a handler for the speech recognized event.  
        recognizer.SpeechRecognized +=   
          new EventHandler<SpeechRecognizedEventArgs>(recognizer_SpeechRecognized);  
  
        // Configure input to the speech recognizer.  
        recognizer.SetInputToDefaultAudioDevice();  
  
        // Start asynchronous, continuous speech recognition.  
        recognizer.RecognizeAsync(RecognizeMode.Multiple);  
  
        // Keep the console window open.  
        while (true)  
        {  
          Console.ReadLine();  
        }  
      }  
    }  
  
    // Handle the SpeechRecognized event.  
    static void recognizer_SpeechRecognized(object sender, SpeechRecognizedEventArgs e)  
    {  
      Console.WriteLine("Recognized text: " + e.Result.Text);  
    }  
  }  
}  
  
```

  
  
## Remarks  
 <span data-ttu-id="65a8d-424">Il riconoscimento <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarAsync%2A> metodo avvia un'operazione asincrona.</span><span class="sxs-lookup"><span data-stu-id="65a8d-424">The recognizer's <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarAsync%2A> method initiates an asynchronous operation.</span></span> <span data-ttu-id="65a8d-425">Il <xref:System.Speech.Recognition.SpeechRecognitionEngine> genera questo evento quando viene completato l'operazione.</span><span class="sxs-lookup"><span data-stu-id="65a8d-425">The <xref:System.Speech.Recognition.SpeechRecognitionEngine> raises this event when it completes the operation.</span></span> <span data-ttu-id="65a8d-426">Per ottenere il <xref:System.Speech.Recognition.Grammar> oggetto che il riconoscimento è stato caricato, usare il <xref:System.Speech.Recognition.LoadGrammarCompletedEventArgs.Grammar%2A> proprietà del controllo associato <xref:System.Speech.Recognition.LoadGrammarCompletedEventArgs>.</span><span class="sxs-lookup"><span data-stu-id="65a8d-426">To get the <xref:System.Speech.Recognition.Grammar> object that the recognizer loaded, use the <xref:System.Speech.Recognition.LoadGrammarCompletedEventArgs.Grammar%2A> property of the associated <xref:System.Speech.Recognition.LoadGrammarCompletedEventArgs>.</span></span> <span data-ttu-id="65a8d-427">Per ottenere l'oggetto corrente <xref:System.Speech.Recognition.Grammar> oggetti è stata caricata la sospensione del riconoscimento, usare il riconoscimento <xref:System.Speech.Recognition.SpeechRecognitionEngine.Grammars%2A> proprietà.</span><span class="sxs-lookup"><span data-stu-id="65a8d-427">To get the current <xref:System.Speech.Recognition.Grammar> objects the recognizer has loaded, use the recognizer's <xref:System.Speech.Recognition.SpeechRecognitionEngine.Grammars%2A> property.</span></span>  
  
 <span data-ttu-id="65a8d-428">Se il riconoscimento è in esecuzione, le applicazioni devono utilizzare <xref:System.Speech.Recognition.SpeechRecognitionEngine.RequestRecognizerUpdate%2A> sospendere il motore di riconoscimento vocale prima il caricamento, scaricamento, abilitazione o la disabilitazione di una grammatica.</span><span class="sxs-lookup"><span data-stu-id="65a8d-428">If the recognizer is running, applications must use <xref:System.Speech.Recognition.SpeechRecognitionEngine.RequestRecognizerUpdate%2A> to pause the speech recognition engine before loading, unloading,  enabling, or disabling a grammar.</span></span>  
  
 <span data-ttu-id="65a8d-429">Quando si crea un delegato <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarCompleted>, si identifica il metodo che gestirà l'evento.</span><span class="sxs-lookup"><span data-stu-id="65a8d-429">When you create a <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarCompleted> delegate, you identify the method that will handle the event.</span></span> <span data-ttu-id="65a8d-430">Per associare l'evento al gestore eventi in uso, aggiungere all'evento un'istanza del delegato.</span><span class="sxs-lookup"><span data-stu-id="65a8d-430">To associate the event with your event handler, add an instance of the delegate to the event.</span></span> <span data-ttu-id="65a8d-431">Il gestore eventi viene chiamato ogni volta che si verifica l'evento, a meno che non venga rimosso il delegato.</span><span class="sxs-lookup"><span data-stu-id="65a8d-431">The event handler is called whenever the event occurs, unless you remove the delegate.</span></span> <span data-ttu-id="65a8d-432">Per altre informazioni sui delegati del gestore eventi, vedere [eventi e delegati](http://go.microsoft.com/fwlink/?LinkId=162418).</span><span class="sxs-lookup"><span data-stu-id="65a8d-432">For more information about event-handler delegates, see [Events and Delegates](http://go.microsoft.com/fwlink/?LinkId=162418).</span></span>  
  
   
  
## Examples  
 <span data-ttu-id="65a8d-433">Nell'esempio seguente crea un sistema di riconoscimento vocale in-process e quindi crea due tipi di grammatiche per il riconoscimento delle parole specifiche e per accettare la dettatura gratuita.</span><span class="sxs-lookup"><span data-stu-id="65a8d-433">The following example creates an in-process speech recognizer, and then creates two types of grammars for recognizing specific words and for accepting free dictation.</span></span> <span data-ttu-id="65a8d-434">Nell'esempio si costruisce una <xref:System.Speech.Recognition.Grammar> oggetto da ognuna delle grammatiche di riconoscimento vocale completati, quindi carica in modo asincrono il <xref:System.Speech.Recognition.Grammar> gli oggetti per il <xref:System.Speech.Recognition.SpeechRecognitionEngine> istanza.</span><span class="sxs-lookup"><span data-stu-id="65a8d-434">The example constructs a <xref:System.Speech.Recognition.Grammar> object from each of the completed speech recognition grammars, then asynchronously loads the <xref:System.Speech.Recognition.Grammar> objects to the <xref:System.Speech.Recognition.SpeechRecognitionEngine> instance.</span></span> <span data-ttu-id="65a8d-435">Gestori per il riconoscimento <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarCompleted> e <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> eventi scrive nella console il nome del <xref:System.Speech.Recognition.Grammar> oggetto utilizzato per eseguire il riconoscimento e il testo del risultato del riconoscimento, rispettivamente.</span><span class="sxs-lookup"><span data-stu-id="65a8d-435">Handlers for the recognizer's <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarCompleted> and <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> events write to the console the name of the <xref:System.Speech.Recognition.Grammar> object that was used to perform the recognition and the text of the recognition result, respectively.</span></span>  
  
```  
using System;  
using System.Speech.Recognition;  
  
namespace SampleRecognition  
{  
  class Program  
  {  
    private static SpeechRecognitionEngine recognizer;  
    public static void Main(string[] args)  
    {  
  
      // Initialize an in-process speech recognition engine and set its input.  
      recognizer = new SpeechRecognitionEngine();  
      recognizer.SetInputToDefaultAudioDevice();  
  
      // Add a handler for the LoadGrammarCompleted event.  
      recognizer.LoadGrammarCompleted +=  
        new EventHandler<LoadGrammarCompletedEventArgs>(recognizer_LoadGrammarCompleted);  
  
      // Add a handler for the SpeechRecognized event.  
      recognizer.SpeechRecognized +=  
        new EventHandler<SpeechRecognizedEventArgs>(recognizer_SpeechRecognized);  
  
      // Create the "yesno" grammar.  
      Choices yesChoices = new Choices(new string[] { "yes", "yup", "yeah" });  
      SemanticResultValue yesValue =  
          new SemanticResultValue(yesChoices, (bool)true);  
      Choices noChoices = new Choices(new string[] { "no", "nope", "neah" });  
      SemanticResultValue noValue =  
          new SemanticResultValue(noChoices, (bool)false);  
      SemanticResultKey yesNoKey =  
          new SemanticResultKey("yesno", new Choices(new GrammarBuilder[] { yesValue, noValue }));  
      Grammar yesnoGrammar = new Grammar(yesNoKey);  
      yesnoGrammar.Name = "yesNo";  
  
      // Create the "done" grammar.  
      Grammar doneGrammar =  
        new Grammar(new Choices(new string[] { "done", "exit", "quit", "stop" }));  
      doneGrammar.Name = "Done";  
  
      // Create a dictation grammar.  
      Grammar dictation = new DictationGrammar();  
      dictation.Name = "Dictation";  
  
      // Load grammars to the recognizer.  
      recognizer.LoadGrammarAsync(yesnoGrammar);  
      recognizer.LoadGrammarAsync(doneGrammar);  
      recognizer.LoadGrammarAsync(dictation);  
  
      // Start asynchronous, continuous recognition.  
      recognizer.RecognizeAsync(RecognizeMode.Multiple);  
  
      // Keep the console window open.  
      Console.ReadLine();  
    }  
  
    // Handle the LoadGrammarCompleted event.   
    static void recognizer_LoadGrammarCompleted(object sender, LoadGrammarCompletedEventArgs e)  
    {  
      string grammarName = e.Grammar.Name;  
      bool grammarLoaded = e.Grammar.Loaded;  
  
      if (e.Error != null)  
      {  
        Console.WriteLine("LoadGrammar for {0} failed with a {1}.",  
        grammarName, e.Error.GetType().Name);  
  
        // Add exception handling code here.  
      }  
  
      Console.WriteLine("Grammar {0} {1} loaded.",  
      grammarName, (grammarLoaded) ? "is" : "is not");  
    }  
  
    // Handle the SpeechRecognized event.  
    static void recognizer_SpeechRecognized(object sender, SpeechRecognizedEventArgs e)  
    {  
      Console.WriteLine("Grammar({0}): {1}", e.Result.Grammar.Name, e.Result.Text);  
  
      // Add event handler code here.  
    }  
  }  
}  
  
```

  
  
## Remarks  
 <span data-ttu-id="65a8d-444">Stringa, numero intero a 64 bit o i dati degli indirizzi della memoria, possono contenere le impostazioni di sistema di riconoscimento.</span><span class="sxs-lookup"><span data-stu-id="65a8d-444">Recognizer settings can contain string, 64-bit integer, or memory address data.</span></span> <span data-ttu-id="65a8d-445">La tabella seguente descrive le impostazioni definite per un'API di riconoscimento vocale Microsoft (SAPI)-riconoscimento conforme.</span><span class="sxs-lookup"><span data-stu-id="65a8d-445">The following table describes the settings that are defined for a Microsoft Speech API (SAPI)-compliant recognizer.</span></span> <span data-ttu-id="65a8d-446">Le seguenti impostazioni devono avere lo stesso intervallo per ogni riconoscimento che supporta l'impostazione.</span><span class="sxs-lookup"><span data-stu-id="65a8d-446">The following settings must have the same range for each recognizer that supports the setting.</span></span> <span data-ttu-id="65a8d-447">Un sistema di riconoscimento conformi SAPI non è necessaria per supportare queste impostazioni e può supportare altre impostazioni.</span><span class="sxs-lookup"><span data-stu-id="65a8d-447">A SAPI-compliant recognizer is not required to support these settings and can support other settings.</span></span>  
  
|<span data-ttu-id="65a8d-448">nome</span><span class="sxs-lookup"><span data-stu-id="65a8d-448">Name</span></span>|<span data-ttu-id="65a8d-449">Descrizione</span><span class="sxs-lookup"><span data-stu-id="65a8d-449">Description</span></span>|  
|----------|-----------------|  
|`ResourceUsage`|<span data-ttu-id="65a8d-450">Specifica l'utilizzo della CPU del motore di riconoscimento.</span><span class="sxs-lookup"><span data-stu-id="65a8d-450">Specifies the recognizer's CPU consumption.</span></span> <span data-ttu-id="65a8d-451">L'intervallo è compreso tra 0 e 100.</span><span class="sxs-lookup"><span data-stu-id="65a8d-451">The range is from 0 to 100.</span></span> <span data-ttu-id="65a8d-452">Il valore predefinito è 50.</span><span class="sxs-lookup"><span data-stu-id="65a8d-452">The default value is 50.</span></span>|  
|`ResponseSpeed`|<span data-ttu-id="65a8d-453">Indica la lunghezza di silenzio alla fine dell'input ambiguo prima del riconoscimento vocale viene completata un'operazione di riconoscimento.</span><span class="sxs-lookup"><span data-stu-id="65a8d-453">Indicates the length of silence at the end of unambiguous input before the speech recognizer completes a recognition operation.</span></span> <span data-ttu-id="65a8d-454">L'intervallo va da 0 a 10.000 millisecondi (ms).</span><span class="sxs-lookup"><span data-stu-id="65a8d-454">The range is from 0 to 10,000 milliseconds (ms).</span></span> <span data-ttu-id="65a8d-455">Questa impostazione corrisponde al riconoscimento <xref:System.Speech.Recognition.SpeechRecognitionEngine.EndSilenceTimeout%2A> proprietà.</span><span class="sxs-lookup"><span data-stu-id="65a8d-455">This setting corresponds to the recognizer's <xref:System.Speech.Recognition.SpeechRecognitionEngine.EndSilenceTimeout%2A> property.</span></span>  <span data-ttu-id="65a8d-456">Default = 150 ms.</span><span class="sxs-lookup"><span data-stu-id="65a8d-456">Default = 150ms.</span></span>|  
|`ComplexResponseSpeed`|<span data-ttu-id="65a8d-457">Indica la lunghezza di silenzio alla fine dell'input ambiguo prima del riconoscimento vocale viene completata un'operazione di riconoscimento.</span><span class="sxs-lookup"><span data-stu-id="65a8d-457">Indicates the length of silence at the end of ambiguous input before the speech recognizer completes a recognition operation.</span></span> <span data-ttu-id="65a8d-458">L'intervallo va da 0 a 10.000 ms.</span><span class="sxs-lookup"><span data-stu-id="65a8d-458">The range is from 0 to 10,000ms.</span></span> <span data-ttu-id="65a8d-459">Questa impostazione corrisponde al riconoscimento <xref:System.Speech.Recognition.SpeechRecognitionEngine.EndSilenceTimeoutAmbiguous%2A> proprietà.</span><span class="sxs-lookup"><span data-stu-id="65a8d-459">This setting corresponds to the recognizer's <xref:System.Speech.Recognition.SpeechRecognitionEngine.EndSilenceTimeoutAmbiguous%2A> property.</span></span> <span data-ttu-id="65a8d-460">Default = 500 ms.</span><span class="sxs-lookup"><span data-stu-id="65a8d-460">Default = 500ms.</span></span>|  
